export const addToCartReducer = (state={cartItems:[]},action) => {
    switch(action.type) {
        case 'CART_ADD_ITEM': {
            const newItem = action.payload
            const check = cartItems.find(item => item.id===newItem.id)
            if(check){
               return cartItems.map(item => item.id===newItem.id? newItem :item)
            }else{
                return cartItems.concat(newItem)
            }

        }
        default:
            return state
    }
}
