//constants
import {
    CART_ADD_ITEM,
    CART_REMOVE_ITEM,
    CART_CLEAR_ITEMS,

    CART_SAVE_SHIPPING_ADDRESS
} from '../Constants/cartConstants'



export const editCartReducer = (state={cartItems:[]},action) => {
    switch(action.type) {
        case CART_ADD_ITEM: {
            const newItem = action.payload
            const check = state.cartItems.find(item => item.id===newItem.id)
            if(check){
               return { ...state,
                   cartItems: state.cartItems.map(item => item.id===newItem.id? newItem :item)
                   }
            }else{
                return {...state,
                        cartItems: state.cartItems.concat(newItem) }
            }

        }
        case CART_REMOVE_ITEM: {
            return{...state,
                    cartItems: state.cartItems.filter(item => item.id!==action.payload)}
            
        }
        case CART_SAVE_SHIPPING_ADDRESS: {
            return{...state, }
        }
        default:
            return state
    }
}
