import { useEffect } from 'react';
import {useDispatch,useSelector} from 'react-redux'
import {Row,Col,ListGroup,Image,Form,Button} from 'react-bootstrap'
import {Link} from 'react-router-dom'

//Actions
import {addToCart } from '../Actions/cartActions'

//My components
import Message from '../Components/Message'


function Cartscreen({match}) {
    const queryParams = new URLSearchParams(window.location.search);
    const qty = parseInt(queryParams.get('qty'));
    const dispatch = useDispatch()

    const cart = useSelector(state => state.cart)
    const {cartItems} = cart
    
    useEffect(()=>{
        if(match.params.id){
            dispatch(addToCart(match.params.id,qty))
        }
    },[dispatch,match.params.id,qty])
    
    console.log(cartItems)
  
    return(
        <Row>
            <Col md={8}>
                <h2>Shopping Cart</h2>
                {cartItems.length?
                                    (<ListGroup variant="flush">
                                        {cartItems.map(item => (
                                            <ListGroup.Item key={item.id}>
                                                <Row>
                                                    <Col xs={3} md={2}>
                                                        <Link to = {`/product/${item.id}`}>
                                                         <Image src={item.image} alt={item.name} fluid/>
                                                        </Link>
                                                       
                                                    </Col>
                                                    <Col xs={5} md={3}>
                                                        <Link className = "text-decoration-none" to = {`/product/${item.id}`}>
                                                            {item.name}
                                                        </Link>
                                                    </Col>
                                                    <Col xs={3} md={2} className="py-2">
                                                    &#8377;{item.price}
                                                    </Col>
                                                    <Col xs={3} md={2} className="py-2">
                                                        <label htmlFor="qty">Qty</label> 
                                                        <select
                                                            id="qty"
                                                            value={item.qty}
                                                            onChange={(e) => dispatch(addToCart(item.product, Number(e.target.value)))}
                                                        >
                                                            {

                                                                [...Array(item.countInStock).keys()].map((x) => (
                                                                    <option key={x + 1} value={x + 1}>
                                                                        {x + 1}
                                                                    </option>
                                                                ))
                                                            }

                                                        </select>
                                                    </Col>
                                                </Row>
                                                

                                            </ListGroup.Item>
                                        ))}
                                    </ListGroup>
                                    )
                                    : <Message variant="info">Your cart is empty. <Link to="/">Start shopping</Link></Message>
                }
            </Col>
            <Col md={4}>

            </Col>
        </Row>
    )
}

export default Cartscreen
