import axios from 'axios'
import { CART_CLEAR_ITEMS } from '../Constants/cartConstants'

import {
    USER_LOGIN_REQUEST,
    USER_LOGIN_SUCCESS,
    USER_LOGIN_FAIL,

    USER_LOGOUT,

    USER_REGISTER_REQUEST,
    USER_REGISTER_SUCCESS,
    USER_REGISTER_FAIL,

    USER_DETAILS_REQUEST,
    USER_DETAILS_SUCCESS,
    USER_DETAILS_FAIL,
    USER_DETAILS_RESET,

    USER_UPDATE_PROFILE_REQUEST,
    USER_UPDATE_PROFILE_SUCCESS,
    USER_UPDATE_PROFILE_FAIL,
    USER_UPDATE_PROFILE_RESET,

    USER_LIST_REQUEST,
    USER_LIST_SUCCESS,
    USER_LIST_FAIL,
    USER_LIST_RESET,

    USER_DELETE_REQUEST,
    USER_DELETE_SUCCESS,
    USER_DELETE_FAIL,
} from '../Constants/userConstants'


export const login = (email,password) => async(dispatch) => {
    try{
        const config = {
            headers:{'Content-type' :'Application/json'}
        }
        dispatch({type:USER_LOGIN_REQUEST})

        const {data} = await axios.post('/api/users/login/',
                        {'username':email,'password':password},
                        config)
        dispatch({type: USER_LOGIN_SUCCESS, payload:data})
        localStorage.setItem('userInfo',JSON.stringify(data))
    }catch(error) {
        dispatch({type: USER_LOGIN_FAIL, 
            payload:error.response&&error.response.data.detail
                    ?error.response.data.detail
                    :error.message})     
    }
}

export const logout = () =>(dispatch) =>{
    localStorage.removeItem('userInfo')
    dispatch({type: USER_LOGOUT})
    dispatch({type: USER_DETAILS_RESET})
    dispatch({type: CART_CLEAR_ITEMS})
}

export const register = (name,email,password) => async(dispatch) => {
    try{
        const config = {
            headers:{'Content-type' :'Application/json'}
        }
        dispatch({type:USER_REGISTER_REQUEST})

        const {data} = await axios.post('/api/users/register/',
                        {'name':name,'email':email,'password':password},
                        config)
        dispatch({type: USER_REGISTER_SUCCESS, payload:data})
        dispatch({type: USER_LOGIN_SUCCESS, payload:data})
        localStorage.setItem('userInfo',JSON.stringify(data))
    }catch(error) {
        dispatch({type: USER_REGISTER_FAIL, 
            payload:error.response&&error.response.data.detail
                    ?error.response.data.detail
                    :error.message})     
    }
}

export const getUserDetails = (id) => async(dispatch,getState) => {
    try{
        const {userLogin} = getState()
        const {userInfo} = userLogin

        const config = {
            headers:{'Content-type' :'Application/json',
                     Authorization: 'Bearer '+userInfo.token
                    }

        }
        dispatch({type:USER_DETAILS_REQUEST})

        const {data} = await axios.get(`/api/users/${id}/`,
                        config)

        dispatch({type: USER_DETAILS_SUCCESS, payload:data})
  
    }catch(error) {
        dispatch({type: USER_DETAILS_FAIL, 
            payload:error.response&&error.response.data.detail
                    ?error.response.data.detail
                    :error.message})     
    }
}

export const updateUserProfile = (user) => async(dispatch,getState) => {
    try{
        const {userLogin} = getState()
        const {userInfo} = userLogin
        console.log(userInfo)
        const config = {
            headers:{'Content-type' :'Application/json',
                     Authorization: 'Bearer '+userInfo.token
                    }
        }

        dispatch({type:USER_UPDATE_PROFILE_REQUEST})

        const {data} = await axios.put(`/api/users/profile/update/`,user,
                        config)

        dispatch({type: USER_UPDATE_PROFILE_SUCCESS, payload:data})
        dispatch({type: USER_LOGIN_SUCCESS,payload:data})
        localStorage.setItem('userInfo', JSON.stringify(data))
  
        }catch(error) {
        dispatch({type: USER_UPDATE_PROFILE_FAIL, 
            payload:error.response&&error.response.data.detail
                    ?error.response.data.detail
                    :error.message})     
    }
}

export const getUserList = () => async(dispatch,getState) => {
    try{
        const {userLogin} = getState()
        const {userInfo} = userLogin

        const config = {
            headers:{'Content-type' :'Application/json',
                     Authorization: 'Bearer '+userInfo.token
                    }

        }
        dispatch({type:USER_LIST_REQUEST})

        const {data} = await axios.get(`/api/users/`,
                        config)

        dispatch({type: USER_LIST_SUCCESS, payload:data})
  
    }catch(error) {
        dispatch({type: USER_LIST_FAIL, 
            payload:error.response&&error.response.data.detail
                    ?error.response.data.detail
                    :error.message})     
    }
}

export const deleteUser = (id) => async(dispatch,getState) => {
    try{
        const {userLogin} = getState()
        const {userInfo} = userLogin

        const config = {
            headers:{'Content-type' :'Application/json',
                     Authorization: 'Bearer '+userInfo.token
                    }

        }
        dispatch({type:USER_DELETE_REQUEST})

        const {data} = await axios.delete(`/api/users/delete/${id}`,
                        config)

        dispatch({type: USER_DELETE_SUCCESS})
  
    }catch(error) {
        dispatch({type: USER_DELETE_FAIL, 
            payload:error.response&&error.response.data.detail
                    ?error.response.data.detail
                    :error.message})     
    }
}

export const updateUser = (user) => async(dispatch,getState) => {
    try{
        const {userLogin} = getState()
        const {userInfo} = userLogin

        const config = {
            headers:{'Content-type' :'Application/json',
                     Authorization: 'Bearer '+userInfo.token
                    }

        }
        dispatch({type:USER_UPDATE_REQUEST})

        const {data} = await axios.delete(`/api/users/delete/${id}`,
                        config)

        dispatch({type: USER_UPDATE_SUCCESS})

        dispatch({type: USER_LIST_SUCCESS,payload:data})
  
    }catch(error) {
        dispatch({type: USER_UPDATE_FAIL, 
            payload:error.response&&error.response.data.detail
                    ?error.response.data.detail
                    :error.message})     
    }
}
